/* Base */
import axios from 'axios'
import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'


export default function Signup() {

    /* Declaring State Object */
    const [email, setEmail] = useState<string>('')
    const [username, setUsername] = useState<string>('')
    const [password, setPassword] = useState<string>('')
    const [error, setError] = useState<string>('')

    const url = 'http://localhost:8000/'
    const navigate = useNavigate()

    /* const { fetchPostOption, data, isPending, error } = useSignup(url) */

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {

        e.preventDefault()

        /* POST Request with Axios*/
        axios.post(url, {email, username, password})

            .then(() => {
                console.log("Data successfully posted")

            }).catch(function (err) {
                console.log("Could not POST data")
                
                /* The request was made and the server responded with a status code that falls out of the range of 2xx */
                if (err.response) {
                    setError(Object.values(err.response.data.error).map(x => x).join(', '))
                    console.log(err.response.data.error);
                    console.log(err.response.status);
                    console.log(err.response.headers);

                /* The request was made but no response was received */
                } else if (err.request) {
                    setError(err.request)
                    console.log(err.request);

                /* Something happened in setting up the request that triggered an Error */
                } else {
                    setError(err.message)
                    console.log('Error', err.message);
                }
              })
    }


    return (
        <form onSubmit={handleSubmit}>
            <label>
                <span>User email:</span>
                <input
                    required 
                    type="email"
                    onChange={(e) => setEmail(e.target.value)}
                    value={email}
                />
            </label>
            <label>
                <span>User name:</span>
                <input
                    required 
                    type="text"
                    onChange={(e) => setUsername(e.target.value)}
                    value={username}
                />
            </label>
            <label>
                <span>User password:</span>
                <input
                    required 
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                />
            </label>
            
            <button>Sign up</button>

            {/* { (error != '') && <p>{error}</p> }
            { isPending && <button className='btn' disabled>Loading</button> }
            { !isPending && <button>Sign up</button> }
 */}
        </form>
    )
}
