/* Base */
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'

/* Authentication Context */
import { useAuthContext } from '../../hooks/useAuthContext'
import { doLogin } from '../../contexts/AuthContext'

/* Bootstrap */
import { Button, Form, InputGroup } from 'react-bootstrap'
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

/* Styling */
import './Signup.css'


export default function Signup() {

    /* Declaring State Object and Constant variables */
    const [email, setEmail] = useState<string>('')
    const [username, setUsername] = useState<string>('')
    const [password, setPassword] = useState<string>('')
    const [error, setError] = useState<string>('')

    const { dispatch } = useAuthContext()

    const url = 'http://localhost:8000/user/signup'

    const navigate = useNavigate()


    /* Declaring Event handler */
    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {

        e.preventDefault()
        
        axios.post(url, {email, username, password})
            .then((res) => {

                console.log('Login successful!')
                console.log('Response:', res)

                dispatch(doLogin(username))
                sessionStorage.setItem("sessionName", username)
                navigate('/')

            })
            .catch((err) => {
                if (err.response) {
                    /* The request was made and the server responded with a status code that falls out of the range of 2xx */
                    console.log(Object.values(err.response.data.error).join(', '))
                    setError(Object.values(err.response.data.error).join(', '))

                } else if (err.request) {
                    /* The request was made but no response was received */
                    console.log(err.request)
                    setError(err.request)

                } else {
                    /* Something happened in setting up the request that triggered an Error */
                    console.log(err.message)
                    setError(err.message)
                }
                console.log(err.config)
            });
    }

    /* Render */
    return (
        <div className='signup_page'>
            <Card className="form_card" style={{ width: '20rem', height: '25rem'}}>
                <Card.Body>
                    <Card.Title className='form_title'>Signup for a new account</Card.Title>
                    <Form onSubmit={handleSubmit}>
                        <Form.Label className='form_label'>Email:</Form.Label>
                        <InputGroup className="signup_form_email">
                            <InputGroup.Text id="basic-addon1">@</InputGroup.Text>
                            <Form.Control 
                                type="email" 
                                placeholder='Enter email'
                                onChange={(e) => setEmail(e.target.value)}
                                value={email}
                            />
                        </InputGroup>
   
                        <Form.Group className="signup_form_username">
                            <Form.Label>Username:</Form.Label>
                            <Form.Control 
                                type="text" 
                                placeholder='Enter username'
                                onChange={(e) => setUsername(e.target.value)}
                                value={username}
                            />
                        </Form.Group>

                        <Form.Group className="signup_form_password">
                            <Form.Label>Password:</Form.Label>
                            <Form.Control 
                                type="password" 
                                placeholder='Enter password'
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                            />
                        </Form.Group>
                                
                        <Button variant='primary' type="submit">Sign up</Button>

                        { (error != '') && <p>{error}</p> }

                    </Form> 
                </Card.Body>
                <Card.Footer>
                    <small className="card_footer">Already have an account? Login</small>
                </Card.Footer>
            </Card>
        </div>
    )
}
