/* Base */
import { useState } from 'react'

/* Bootstrap */
import { Modal } from 'react-bootstrap'


export default function ErrorModal(errorMessage: string | null) {

    const [ showModal, setShowModal ] = useState<boolean>(true)

    return (
        <Modal className="error_modal" show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header>Error<button className="error_modal_close" onClick={()=>setShowModal(false)}>X</button></Modal.Header>
            <Modal.Body>
                <>
                    { errorMessage }
                </>
            </Modal.Body>
        </Modal>
    )
}
