/* Base */
import { useState } from 'react'

/* Bootstrap */
import { Modal } from 'react-bootstrap'


export default function ErrorModal( { errorMessage, showModal, onClose }: any) {

    return (
        <Modal className="error_modal" show={showModal} onHide={onClose}>
            <Modal.Header>Error<button className="error_modal_close" onClick={onClose}>X</button></Modal.Header>
            <Modal.Body>
                <>
                    { errorMessage }
                </>
            </Modal.Body>
        </Modal>
    )
}
