import { useState, useEffect } from 'react'

interface IGameStatus {
    firstPlayer: string | null, 
    secondPlayer: string | null, 
    currentTurn: string | null, 
    gameOver: boolean, 
    gameWinner: string | null, 
    isDraw: boolean
}


export default function useTicTacToeLog(backendOutcome: any) {

    const [ error, setError ] = useState<string>("")
    const [ gameLog, setGameLog ] = useState<string[]>([])
    const [ gameStatus, setGameStatus ] = useState<IGameStatus>({ firstPlayer: null, secondPlayer: null, currentTurn: null, gameOver: false, gameWinner: null, isDraw: false})

    useEffect(() => {
        
        if (backendOutcome.status === "error") {
            switch (backendOutcome.category) {
                case "waiting_opponent":
                    setError("Please wait for an opponent to join the game")
                    break
                case "game_over":
                    setError("The game is already over")
                    break
                case "wrong_turn":
                    setError("This is not your turn. You cannot play on oppent's turn")
                    break
                case "square_taken":
                    setError("The selected square is already taken. Please choose another square")
                    break
                default:
                    break
            }
        }
        else if (backendOutcome.status === "success") {
            switch (backendOutcome.category) {
                case "game_creation":
                    setGameLog([ ...gameLog, ""])
                    break
                case "game_join":
                    setError("The game is already over")
                    break
                case "game_leave":
                    setError("This is not your turn. You cannot play on oppent's turn")
                    break
                case "turn_change":
                    setError("The selected square is already taken. Please choose another square")
                    break
                case "is_winner":
                    setError("The selected square is already taken. Please choose another square")
                    break
                case "is_draw":
                    setError("The selected square is already taken. Please choose another square")
                    break
                default:
                    break
            }
        }

    }, [backendOutcome, gameLog, gameStatus])
        

    return { gameStatus, gameLog, error }
}
