import { useState, useEffect } from 'react'

interface IGameStatus {
    firstPlayer: string | null, 
    secondPlayer: string | null, 
    currentTurn: string | null, 
    gameOver: boolean, 
    gameWinner: string | null, 
    isDraw: boolean
}


export default function useTicTacToeLog(backendOutcome: JSON | null) {

    const [ error, setError ] = useState<string>("")
    const [ gameStatus, setGameStatus ] = useState<IGameStatus>({ firstPlayer: null, secondPlayer: null, currentTurn: null, gameOver: false, gameWinner: null, isDraw: false})

    useEffect(() => {
        
        console.log(backendOutcome)

    }, [backendOutcome, gameStatus])
        

    return { gameStatus, error }
}
