/* Base */
import { useState } from 'react'

/* Bootstrap */
import { Modal } from 'react-bootstrap'


export default function ErrorModal( { errorMessage, showModal, onClose }: any) {

    const [ isOpen, setIsOpen ] = useState<boolean>(showModal)

    return (
        <Modal className="error_modal" show={isOpen} onHide={() => {onClose; setIsOpen(false)}}>
            <Modal.Header>Error<button className="error_modal_close" onClick={() => {onClose; setIsOpen(false)}}>X</button></Modal.Header>
            <Modal.Body>
                <>
                    { errorMessage }
                </>
            </Modal.Body>
        </Modal>
    )
}
